function hidePopover(a){$(a).popover("hide")}function changeToggleButton(b,a){$("div[toggle-buttons-name="+b+"]").toggleButtons("setState",a)}function gotoTop(){$("#top_arrow").hide();$(function(){$(window).scroll(function(){if($(window).scrollTop()>100){$("#top_arrow").fadeIn(400)}else{$("#top_arrow").fadeOut(400)}});$("#top_arrow").click(function(){$("body,html").animate({scrollTop:0},400);return false})})}function hideFixedBotton(){$(".fixedBotton").hide();$("tr").mouseover(function(a){$(this).find(".fixedBotton").show();a.preventDefault()});$("tr").mouseleave(function(a){$(this).find(".fixedBotton").hide();a.preventDefault()})};